mapper.select:
SELECT A.MEMBER_NAME , MEMBER_SEQ , DATE_FORMAT(A.MEMBER_BIRTHDAY,'%Y년 %c월 %e일') AS MEMBER_BIRTHDAY , CASE MEMBER_GENDER WHEN 'M' THEN '남' WHEN 'F' THEN '여' ELSE '?' END AS MEMBER_GENDER , B.VILLAGE_NAME , A.PHONE_NUMBER , SF_GET_CODE_NAME('AA',MEMBER_POSITION) AS MEMBER_POSITION , MEMBER_JOB FROM MEMBER A, VILLAGE B WHERE A.VILLAGE_SEQ = B.VILLAGE_SEQ AND B.VILLAGE_USE_YN = 'Y'
AND A.MEMBER_NAME LIKE CONCAT('%',#{name},'%')
AND A.VILLAGE_SEQ = #{village}
AND A.MEMBER_POSITION = #{memberPosition}
ORDER BY A.MEMBER_NAME ASC
